informationNeed.text:
		      SELECT ?document
          WHERE {
            {
              {
                ?document <dc:creator> ?creator .
                FILTER (CONTAINS(?creator, "Lopez"))
              }
              UNION
              {
                ?document <dc:contributor> ?contributor .
                FILTER (CONTAINS(?contributor, "Lopez"))
              }
            }
            {
              ?document <dc:publisher> ?publisher .
              FILTER (CONTAINS(?publisher, "Informatica"))
            }
          }
