informationNeed.text:

SELECT ?document 
WHERE {
  {
    ?document <dc:subject> ?subject .
    FILTER (CONTAINS(?subject, "animacion") || CONTAINS(?subject, "vision por computador"))
  }
  {
    ?document <dc:date> ?date .
    FILTER (?date >= 2003)
  }
  {
    ?document <dc:language> ?lang .
    FILTER (?lang = "spa")
  }
}

		      SELECT ?document
          WHERE {
            {
              {
                ?document <dc:creator> ?creator .
                FILTER (CONTAINS(?creator, "Lopez"))
              }
              UNION
              {
                ?document <dc:contributor> ?contributor .
                FILTER (CONTAINS(?contributor, "Lopez"))
              }
            }
            {
              ?document <dc:publisher> ?publisher .
              FILTER (CONTAINS(?publisher, "Informatica"))
            }
          }
